		isc_619.isc_647 = TRUE;
		/*X.RDB$QUERY_HEADER.NULL*/
		isc_619.isc_646 = TRUE;
		/*X.RDB$EDIT_STRING.NULL*/
		isc_619.isc_645 = TRUE;
		/*X.RDB$DESCRIPTION.NULL*/
		isc_619.isc_644 = TRUE;
		/*X.RDB$FIELD_POSITION.NULL*/
