			case att_field_position:
				/*X.RDB$FIELD_POSITION.NULL*/
				isc_619.isc_643 = FALSE;
				/*X.RDB$FIELD_POSITION*/
				isc_619.isc_652 = (USHORT) get_numeric(tdgbl);
