			isc_92.isc_93 = -1;
		/*END_MODIFY*/
		isc_96.isc_97 = isc_92.isc_93;
		isc_96.isc_98 = isc_92.isc_95;
                isc_send (isc_status, (FB_API_HANDLE*) &req_handle5, (short) 1, (short) 10, &isc_96, (short) 0);;
		/*ON_ERROR*/
		if (isc_status [1])
		   {
			general_on_error ();
		/*END_ERROR;*/
		   }
